import React, { useState } from 'react';

import AddTaskForm from './components/AddTaskForm.jsx';
import UpdateForm from './components/UpdateForm.jsx';
import ToDo from './components/ToDo.jsx';

import 'bootstrap/dist/css/bootstrap.min.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCircleCheck, faPen, faTrashCan } from '@fortawesome/free-solid-svg-icons' 

import './App.css';


function App() {
  // Tasks (ToDo List) State
  //main state storing todo tasks
  const [toDo, setToDo] = useState([])

  // Temp State
  //use inputs to add new task or update
  const [newTask, setNewTask] = useState('');
  const [updateData, setUpdateData] = useState('');

  //functions that run the app
  // Add task 
  const addTask = () => {
    if(newTask){
      let num = toDo.length + 1;
      let newEntry = {id:num, title:newTask, status:false}
      setToDo([...toDo, newEntry])
      setNewTask('');
    }
  }

  // Delete task 
  const deleteTask = (id) => {
    let newTasks = toDo.filter(task => task.id !== id)
    setToDo(newTasks);
  }

  // Mark task as done or completed
  const markDone = (id) => {
    let newTask = toDo.map( task => {
      if(task.id === id ){
        return({...task, status: !task.status })
      }
      return task;
    })
    setToDo(newTask);
  }

  // Cancel update
  const cancelUpdate = () => {
    setUpdateData('');
  }

  // Change task for update
  const changeTask = (e) => {
    let newEntry ={
      id:updateData.id,
      title:e.target.value,
      status:updateData.status ? true : false
    }
    setUpdateData(newEntry);
  }

  // Update task
  const updateTask = () => {
    let filterRecords = [...toDo].filter( task => task.id !== updateData.id);
    let updatedObject = [...filterRecords,updateData]
    setToDo(updatedObject);
    setUpdateData('')
  }

  return (
    <div className="container App">
      <br></br>
      <h2>To Do list App (Reactjs)</h2>
      <br></br> 
      {/* Form to update task */}
      {updateData && updateData ? (
        <>
          <div className='row'>
          <div className='col'>
            <input value={updateData && updateData.title} onChange={(e)=>changeTask(e)} className='form-control form-control-lg'></input>
          </div>
          <div className='col-auto'>
            <button onClick={updateTask} className='btn btn-lg btn-success mr-20'>Update</button>
            <button onClick={cancelUpdate} className='btn btn-lg btn-warning'>Cancel</button>
          </div>
          </div>
          <br></br>
        </>
      ): (
        <AddTaskForm
        />
      )}

      {/* Display ToDos */}

      {toDo && toDo.length ? '' : 'No Tasks...'}

      {toDo && toDo
        .sort((a,b)=> a.id > b.id ? 1 : -1)
        .map((task, index ) => {
          return(
            <React.Fragment key={task.id}>
              <div className='col taskBg'>
                <div className={task.status ? 'done' : ''}>
                <span className='taskNumber'>{index + 1}</span>
                <span className='taskText'>{task.title}</span>
                </div>
                <div className='iconsWrap'>
                  <span className='check' title='completed / Not completed' onClick={(e)=> markDone(task.id)}>
                    <FontAwesomeIcon icon={faCircleCheck}></FontAwesomeIcon>
                  </span>

                  {task.status ? null : (
                    <span className='edit' title='Edit' onClick={()=> setUpdateData({
                      id: task.id,
                      title: task.title,
                      status: task.status ? true : false
                    })}>
                    <FontAwesomeIcon icon={faPen}></FontAwesomeIcon>
                    </span>
                  )}

                  <span className='delete' title='Delete' onClick={()=> deleteTask(task.id)}>
                    <FontAwesomeIcon icon={faTrashCan}></FontAwesomeIcon>
                  </span>
                </div>
              </div>
            </React.Fragment>
          )
        })
      }

    </div>
  );
}

export default App;
